var soap = require("../index")

var url  = "http://www.jiangxi.com/WebServiceTest?wsdl"

// var url  = "http://user.syyx.com/WebService/UserService.asmx?wsdl"

soap.createClient(url, function(err, client) {
    if (err) {        
        console.log("err" + err)
        return
    }

    var args = { no1 : 1, no2: 2}
    client.add(args, function(err, result) {
        if (err) {
            console.log(err)
            return
        }

        console.log(result.result)

    }) 

})

